import React, { useEffect, useState, useCallback } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';

var stompClient = null;

const ChatRoom = () => {
    const [privateChats, setPrivateChats] = useState(new Map());
    const [publicChats, setPublicChats] = useState([]);
    const [tab, setTab] = useState("CHATROOM");
    const [loggedIn, setLoggedIn] = useState(false);
    const [userGroups, setUserGroups] = useState([]);
    const [userData, setUserData] = useState({
        username: '',
        password: '',
        receivername: '',
        connected: false,
        aaaa: '' // Thay đổi từ message sang content
    });

    const connect = useCallback((username) => {
        let Sock = new SockJS('http://localhost:8080/ws');
        stompClient = over(Sock);
        stompClient.connect({}, () => onConnected(username), onError);
    }, []);

    useEffect(() => {
        const savedUserData = JSON.parse(sessionStorage.getItem('userData'));
        if (savedUserData) {
            setUserData({ ...savedUserData });
            setLoggedIn(true);
            connect(savedUserData.username);
            fetchUserGroups(savedUserData.username); // Gọi API để lấy danh sách nhóm khi người dùng đăng nhập
        }
    }, [connect]);

    const fetchUserGroups = (username) => {
        fetch(`http://localhost:8080/api/user/${username}/groups`)
            .then(response => response.json())
            .then(data => {
                setUserGroups(data);
            })
            .catch(error => console.error('Error fetching user groups:', error));
    };


    const onConnected = (username) => {
        setLoggedIn(true);
        stompClient.subscribe('/chatroom/public', onMessageReceived);
        stompClient.subscribe('/user/' + username + '/private', onPrivateMessage);
        userJoin(username);
    };

    const userJoin = (username) => {
        var chatMessage = {
            senderName: username,
            status: "JOIN"
        };
        stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
    };

    const onMessageReceived = (payload) => {
        var payloadData = JSON.parse(payload.body);
        switch (payloadData.status) {
            case "JOIN":
                if (!privateChats.get(payloadData.senderName)) {
                    privateChats.set(payloadData.senderName, []);
                    setPrivateChats(new Map(privateChats));
                }
                break;
            case "MESSAGE":
                publicChats.push(payloadData);
                setPublicChats([...publicChats]);
                break;
            default:
                console.log(`Unknown status: ${payloadData.status}`);
                break;
        }
    };

    const onPrivateMessage = (payload) => {
        console.log(payload);
        var payloadData = JSON.parse(payload.body);
        if (privateChats.get(payloadData.senderName)) {
            privateChats.get(payloadData.senderName).push(payloadData);
            setPrivateChats(new Map(privateChats));
        } else {
            let list = [];
            list.push(payloadData);
            privateChats.set(payloadData.senderName, list);
            setPrivateChats(new Map(privateChats));
        }
    };

    const onError = (err) => {
        console.log(err);
    };

    const handleMessage = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, content: value });
    };

    const sendValue = () => {
        if (stompClient) {
            var chatMessage = {
                senderName: userData.username,
                content: userData.content, // Đặt giá trị content
                status: "MESSAGE"
            };

            if (tab === "CHATROOM") {
                // Gửi tin nhắn vào chatroom
                setPublicChats(prevChats => [...prevChats, chatMessage]);
                stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
            } else {
                // Gửi tin nhắn vào nhóm hiện tại
                const groupId = userGroups.find(group => group.name === tab)?.id;
                if (groupId) {
                    fetch(`http://localhost:8080/api/group/${groupId}/message`, {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify(chatMessage),
                    })
                    .then(response => {
                        // Xử lý kết quả nếu cần
                        if (response.ok) {
                            // Thêm tin nhắn vào danh sách chat
                            setPublicChats(prevChats => [...prevChats, chatMessage]);
                        } else {
                            console.error('Error sending group message:', response.statusText);
                        }
                    })
                    .catch(error => console.error('Error sending group message:', error));
                } else {
                    console.error('Group not found:', tab);
                }
            }

            // Reset nội dung tin nhắn
            setUserData({ ...userData, content: "" });
        }
    };

    const sendGroupValue = (groupName) => {
        if (stompClient) {
            const chatMessage = {
                senderName: userData.username,
                content: userData.content,
                status: "MESSAGE"
            };

            const group = userGroups.find(group => group.name === groupName);
            if (group) {
                fetch(`http://localhost:8080/api/group/${group.id}/message`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(chatMessage),
                })
                .then(response => {
                    if (response.ok) {
                        setPublicChats(prevChats => [...prevChats, chatMessage]);
                    } else {
                        console.error('Error sending group message:', response.statusText);
                    }
                })
                .catch(error => console.error('Error sending group message:', error));
            } else {
                console.error('Group not found:', groupName);
            }

            setUserData({ ...userData, content: "" });
        }
    };

    const sendPrivateValue = () => {
        if (stompClient) {
            var chatMessage = {
                senderName: userData.username,
                receiverName: tab,
                content: userData.content, // Đặt giá trị content
                status: "MESSAGE"
            };

            const newPrivateChats = new Map(privateChats);
            if (userData.username !== tab) {
                if (newPrivateChats.get(tab)) {
                    newPrivateChats.get(tab).push(chatMessage);
                } else {
                    newPrivateChats.set(tab, [chatMessage]);
                }
                setPrivateChats(newPrivateChats);
            }

            setUserData({ ...userData, content: "" });
            stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage));
        }
    };

    const handleUsername = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, username: value });
    };

    const handlePassword = (event) => {
        const { value } = event.target;
        setUserData({ ...userData, password: value });
    };

    const login = () => {
        fetch('http://localhost:8080/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username: userData.username, password: userData.password }),
        })
        .then(response => response.text())
        .then(data => {
            if (data === "Đăng nhập thành công!") {
                sessionStorage.setItem('userData', JSON.stringify({ username: userData.username }));
                setLoggedIn(true);
                connect(userData.username);
            } else {
                alert(data);
            }
        })
        .catch((error) => {
            console.error('Error:', error);
        });
    };

    return (
        <div className="container">
            {loggedIn ?
                <div className="chat-box">
                    <div className="member-list">
                        <ul>
                            <li onClick={() => setTab("CHATROOM")} className={`member ${tab === "CHATROOM" && "active"}`}>Chatroom</li>
                            {[...privateChats.keys()].map((name, index) => (
                                <li onClick={() => setTab(name)} className={`member ${tab === name && "active"}`} key={index}>{name}</li>
                            ))}
                        </ul>
                        <ul>
                        {userGroups.map((group, index) => (
                                <li onClick={() => setTab(group.name)} className={`member ${tab === group.name && "active"}`} key={index}>
                                    {group.name}
                                    <button onClick={() => sendGroupValue(group.name)}>Send Message</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                    {tab === "CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {publicChats.map((chat, index) => (
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">{chat.content}</div> {/* Sử dụng content */}
                                    {chat.senderName === userData.username && <div className="avatar self">{chat.senderName}</div>}
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="enter the message" value={userData.content} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendValue}>send</button>
                        </div>
                    </div>}
                    {tab !== "CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {[...privateChats.get(tab)].map((chat, index) => (
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">{chat.content}</div> {/* Sử dụng content */}
                                    {chat.senderName === userData.username && <div className="avatar self">{chat.senderName}</div>}
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="enter the message" value={userData.content} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendPrivateValue}>send</button>
                        </div>
                    </div>}
                </div>
                :
                <div className="login">
                    <input
                        id="user-name"
                        placeholder="Enter your name"
                        name="userName"
                        value={userData.username}
                        onChange={handleUsername}
                        margin="normal"
                    />
                    <input
                        type="password"
                        placeholder="Enter your password"
                        name="password"
                        value={userData.password}
                        onChange={handlePassword}
                        margin="normal"
                    />
                    <button type="button" onClick={login}>
                        Login
                    </button>
                </div>}
        </div>
    );

};

export default ChatRoom;
